package study;

public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		
		System.out.printf("%d + %d = %d%n", a, b, a+b);
		System.out.println(a + "+" + b +"="+(a+b));
		System.out.println(a + "-" + b +"="+(a-b));
		System.out.println(a + "*" + b +"="+(a*b));
		System.out.println(a + "/" + b +"="+(a/b));

	}

}
